drop table applications;create table applications(id INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), number_of_pilot_jobs INTEGER, input_sendbox VARCHAR(100), output_sendbox VARCHAR(100), status VARCHAR(50), time_start timestamp, time_finish timestamp, top_application_id VARCHAR(100));
drop table pilots;create table pilots(id INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), pilot_id VARCHAR(100), status VARCHAR(50), application_id INTEGER, time_start timestamp, time_last_check_in timestamp, time_finish timestamp, top_application_id VARCHAR(100));